import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';
import Register from './components/Register'; // Adjust the path if necessary
import Login from './components/Login'; // Adjust the path if necessary
import Nav from './components/Nav';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/test')  // Specify the full URL
      .then(response => setData(response.data.message))
      .catch(error => console.error(error));
  }, []);  

  return (
    <Router>
      <div className="flex items-center justify-center h-screen bg-gray-200">
        <Routes>
          <Route path="/register" component={Register} />
          <Route path="/login" component={Login} />
          <Route path="/" element={<h1 className="text-4xl font-bold text-blue-500">{data || 'Loading...'}</h1>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
